module.exports = {
	processRequest: function(req,mid){
		req.once('end',function(){
			try{
				req.flash = JSON.parse(unescape(req.cookie.flash));
			}catch(e){
			}
		});
		mid.emit('done');
	},
	processResponse: function(res){
		res.setHeader('set-cookie','flash=; expires='+new Date().toUTCString()+'; path=/');
	}
};
