module.exports = {
	processRequest: function(req,mid){
		req.wants = function(){
			var accept = req.headers['accept'] || '';
			if (accept.indexOf('html') !== -1) {
				return 'html';
			};
			if (accept.indexOf('json') !== -1 || accept.indexOf('javascript') !== -1) {
				return 'json';
			};
			if (accept.indexOf('xml') !== -1) {
				return 'xml';
			};
		}
		mid.emit('done');
	},
	type:'request'
}
